import React, { useState } from 'react';

function RegistrationForm() {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  });

  const [touched, setTouched] = useState({
    username: false,
    email: false,
    password: false,
    confirmPassword: false
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleBlur = (e: React.FocusEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name } = e.target;
    setTouched(prevTouched => ({ ...prevTouched, [name]: true }));
  };

  const validate = () => {
    const errors: Record<string, string> = {};

    if (!formData.username.trim()) {
      errors.username = 'Username is required.';
    } else if (formData.username.length < 3) {
      errors.username = 'Username must be at least 3 characters.';
    }

    if (!formData.email.trim()) {
      errors.email = 'Email is required.';
    } else if (!/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/g.test(formData.email)) {
      errors.email = 'Invalid email format.';
    }

    if (!formData.password) {
      errors.password = 'Password is required.';
    } else if (formData.password.length < 8) {
      errors.password = 'Password must be at least 8 characters.';
    }

    if (!formData.confirmPassword) {
      errors.confirmPassword = 'Confirm Password is required.';
    } else if (formData.confirmPassword !== formData.password) {
      errors.confirmPassword = 'Passwords do not match.';
    }

    return errors;
  };

  const errors = validate();
  const isFormValid = Object.keys(errors).length === 0 &&
                      Object.values(formData).every(value => value.trim() !== ''); // Ensure all fields are filled even if error obj is empty due to initial state

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Mark all fields as touched on submit attempt to show all errors
    setTouched({
      username: true,
      email: true,
      password: true,
      confirmPassword: true
    });

    if (isFormValid) {
      console.log('Registration Data:', formData);
      alert('Registration Successful!');
      setFormData({ username: '', email: '', password: '', confirmPassword: '' }); // Clear form
      setTouched({ username: false, email: false, password: false, confirmPassword: false }); // Reset touched state
    } else {
      alert('Please fix the errors in the form.');
    }
  };

  const getErrorMessage = (fieldName: keyof typeof formData) => {
    return touched[fieldName] && errors[fieldName] ? (
      <p style={{ color: 'red', fontSize: '0.8em', margin: '5px 0 0 0' }}>{errors[fieldName]}</p>
    ) : null;
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '15px', maxWidth: '400px', margin: '40px auto', padding: '25px', border: '1px solid #ddd', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0,0,0,0.1)' }}>
      <h2>Register</h2>
      <div>
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          name="username"
          value={formData.username}
          onChange={handleChange}
          onBlur={handleBlur}
          style={{ borderColor: (touched.username && errors.username) ? 'red' : '' }}
        />
        {getErrorMessage('username')}
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          onBlur={handleBlur}
          style={{ borderColor: (touched.email && errors.email) ? 'red' : '' }}
        />
        {getErrorMessage('email')}
      </div>
      <div>
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          onBlur={handleBlur}
          style={{ borderColor: (touched.password && errors.password) ? 'red' : '' }}
        />
        {getErrorMessage('password')}
      </div>
      <div>
        <label htmlFor="confirmPassword">Confirm Password:</label>
        <input
          type="password"
          id="confirmPassword"
          name="confirmPassword"
          value={formData.confirmPassword}
          onChange={handleChange}
          onBlur={handleBlur}
          style={{ borderColor: (touched.confirmPassword && errors.confirmPassword) ? 'red' : '' }}
        />
        {getErrorMessage('confirmPassword')}
      </div>

      <button type="submit" disabled={!isFormValid}>Register</button>
    </form>
  );
}

