import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';

// Define the validation schema using Yup
const schema = yup.object({
  name: yup.string().required('Name is required'),
  email: yup.string().email('Invalid email').required('Email is required'),
  message: yup.string().required('Message is required')
}).required(); // .required() at the end ensures the whole schema is required (often for object schemas)

function HookFormBasic() {
  // Initialize useForm, passing the resolver for Yup validation
  const { register, handleSubmit, formState: { errors, isSubmitting } } = useForm({
    resolver: yupResolver(schema)
  });

  // Define the onSubmit handler
  const onSubmit = (data) => {
    console.log('Form submitted:', data);
    // In a real app, you would send 'data' to a server
    // Simulate API call
    return new Promise(resolve => setTimeout(() => {
      alert(JSON.stringify(data, null, 2));
      resolve();
    }, 1000));
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <div>
        <label htmlFor="name">Name:</label>
        <input id="name" {...register('name')} />
        {errors.name && <p className="error" style={{color: 'red'}}>{errors.name.message}</p>}
      </div>

      <div>
        <label htmlFor="email">Email:</label>
        <input id="email" type="email" {...register('email')} />
        {errors.email && <p className="error" style={{color: 'red'}}>{errors.email.message}</p>}
      </div>

      <div>
        <label htmlFor="message">Message:</label>
        <textarea id="message" {...register('message')} />
        {errors.message && <p className="error" style={{color: 'red'}}>{errors.message.message}</p>}
      </div>

      <button type="submit" disabled={isSubmitting}>
        {isSubmitting ? 'Submitting...' : 'Submit'}
      </button>
    </form>
  );
}

export default HookFormBasic;

