import React, { useState, useEffect, useCallback } from 'react';

// Helper for debouncing a function
const debounce = (func, delay) => {
  let timeoutId;
  return (...args) => {
    clearTimeout(timeoutId);
    timeoutId = setTimeout(() => {
      func.apply(this, args);
    }, delay);
  };
};

function WindowDimensionsHooks() {
  const [dimensions, setDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  // Using useCallback to memoize the debounced function, preventing re-creation on every render
  const handleResize = useCallback(
    debounce(() => {
      setDimensions({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    }, 200), // Debounce by 200ms
    [] // Dependency array for useCallback ensures the debounced function is created only once
  );

  useEffect(() => {
    window.addEventListener('resize', handleResize);

    // Cleanup function: runs when component unmounts or before effect re-runs
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [handleResize]); // Dependency array: re-run effect if handleResize changes (which it won't due to useCallback([]))

  return (
    <div style={{ padding: '20px', border: '1px solid #ccc', margin: '20px', display: 'inline-block' }}>
      <h2>Window Dimensions (Functional Component)</h2>
      <p>Width: {dimensions.width}px</p>
      <p>Height: {dimensions.height}px</p>
    </div>
  );
}

export default WindowDimensionsHooks;

