import React, { useState } from 'react';

function UserRegistrationForm() {
  const [email, setEmail] = useState('');
  const [emailError, setEmailError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const validateEmail = (inputEmail) => {
    if (!inputEmail.trim()) {
      return 'Email cannot be empty.';
    }
    if (!/^[\w-]+(\.[\w-]+)*@([a-z0-9-]+(\.[a-z0-9-]+)*?\.[a-z]{2,6}|(\d{1,3}\.){3}\d{1,3})(:\d{4})?$/.test(inputEmail)) {
      return 'Please enter a valid email address.';
    }
    return ''; // No error
  };

  const handleChange = (e) => {
    const newEmail = e.target.value;
    setEmail(newEmail);
    setEmailError(validateEmail(newEmail)); // Validate on change
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationMessage = validateEmail(email);
    if (validationMessage) {
      setEmailError(validationMessage);
      return;
    }

    setIsSubmitting(true);
    console.log('Submitting email:', email);

    // Simulate API call
    try {
      await new Promise(resolve => setTimeout(resolve, 1500));
      alert('Registration successful for: ' + email);
      setEmail(''); // Clear form on success
      setEmailError('');
    } catch (error) {
      console.error('Submission failed:', error);
      setEmailError('Submission failed. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '300px', margin: '20px auto', padding: '20px', border: '1px solid #ccc', borderRadius: '5px' }}>
      <h2>Register</h2>
