import { useState, useEffect, useCallback } from 'react';

// Helper for debouncing a function
const debounce = (func, delay) => {
  let timeoutId;
  return (...args) => {
    clearTimeout(timeoutId);
    timeoutId = setTimeout(() => {
      func.apply(this, args);
    }, delay);
  };
};

function useWindowDimensions(debounceDelay = 200) {
  const [dimensions, setDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  // Using useCallback to memoize the debounced function, ensuring it's stable across renders
  const handleResize = useCallback(
    debounce(() => {
      setDimensions({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    }, debounceDelay), // Use the passed debounceDelay
    [debounceDelay] // Recalculate handleResize if debounceDelay changes
  );

  useEffect(() => {
    // Add event listener
    window.addEventListener('resize', handleResize);

    // Initial dimensions in case they changed before first resize event
    // (though useState already initializes with current innerWidth/Height)
    // handleResize(); // Can optionally call here to ensure initial value reflects debounced logic

    // Cleanup function: remove event listener when component unmounts
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [handleResize]); // Dependency array: Re-run effect if handleResize function changes (only if debounceDelay changes)

  return dimensions;
}

export default useWindowDimensions;

