import React, { useRef, useEffect, useState } from 'react';

function SearchInputComponent() {
  const inputRef = useRef(null);
  const [searchText, setSearchText] = useState('');

  // 1. Auto-focus on mount using useEffect with an empty dependency array
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []); // The empty array ensures this effect runs only once after the initial render

  // 2. Handler for the refocus button
  const handleRefocusClick = () => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  };

  const handleChange = (e) => {
    setSearchText(e.target.value);
  };

  return (
    <div style={{ padding: '20px', border: '1px solid #ccc', margin: '20px auto', maxWidth: '400px', display: 'flex', flexDirection: 'column', gap: '15px' }}>
      <h2>Search Bar</h2>
      <label htmlFor="search-input" style={{ fontWeight: 'bold' }}>Search Query:</label>
      <input
        id="search-input"
        ref={inputRef} // Attach the ref to the input element
        type="text"
        value={searchText}
        onChange={handleChange}
        placeholder="Enter your search term..."
        style={{ padding: '8px', fontSize: '1em' }}
        aria-label="Search input field"
      />
      <p>Current Search: {searchText}</p>
      <button onClick={handleRefocusClick} style={{ padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
        Re-focus Search
      </button>
    </div>
  );
}

export default SearchInputComponent;

