import React, { useState, useEffect, useCallback } from 'react';

// --- hooks/useWindowSize.js --- START
const debounce = (func, delay) => {
  let timeoutId;
  return (...args) => {
    clearTimeout(timeoutId);
    timeoutId = setTimeout(() => {
      func.apply(this, args);
    }, delay);
  };
};

function useWindowSize(debounceDelay = 200) {
  const [dimensions, setDimensions] = useState({
    width: typeof window !== 'undefined' ? window.innerWidth : 0,
    height: typeof window !== 'undefined' ? window.innerHeight : 0,
  });

  const handleResize = useCallback(
    debounce(() => {
      setDimensions({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    }, debounceDelay),
    [debounceDelay] // `handleResize` function itself will only change if `debounceDelay` changes
  );

  useEffect(() => {
    // Guard against SSR environments where `window` might not be defined
    if (typeof window === 'undefined') return;

    // Add event listener
    window.addEventListener('resize', handleResize);

    // Cleanup function: remove event listener when component unmounts
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [handleResize]); // Effect re-runs only if `handleResize` changes (i.e., `debounceDelay` changes)

  return dimensions;
}
// --- hooks/useWindowSize.js --- END

// --- components/LargeScreenNav.js --- START
function LargeScreenNav() {
  return (
    <nav style={{ display: 'flex', gap: '20px' }}>
