import React, { useState, useEffect, useCallback } from 'react';

// Helper for throttling function calls
const throttle = (func, delay) => {
  let timeoutId = null;
  let lastArgs = null;
  let lastThis = null;

  return function(...args) {
    lastArgs = args;
    lastThis = this;

    if (!timeoutId) {
      timeoutId = setTimeout(() => {
        func.apply(lastThis, lastArgs);
        timeoutId = null;
        lastArgs = null;
        lastThis = null;
      }, delay);
    }
  };
};

// Custom hook to get throttled window dimensions
function useThrottledWindowDimensions(delay = 200) {
  const [dimensions, setDimensions] = useState({
    width: typeof window !== 'undefined' ? window.innerWidth : 0,
    height: typeof window !== 'undefined' ? window.innerHeight : 0,
  });

  const updateDimensions = useCallback(
    throttle(() => {
      setDimensions({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    }, delay),
    [delay] // Recalculate throttled function if delay changes
  );

  useEffect(() => {
    if (typeof window === 'undefined') return;

    window.addEventListener('resize', updateDimensions);
    updateDimensions(); // Set initial dimensions

    return () => {
      window.removeEventListener('resize', updateDimensions);
    };
  }, [updateDimensions]); // Dependency on updateDimensions ensures effect re-runs if throttled function changes

  return dimensions;
}

// Component using the custom hook
function ThrottledWindowDimensionsDisplay() {
  const { width, height } = useThrottledWindowDimensions(100);

  return (
    <div style={{ padding: '20px', border: '1px solid #ccc' }}>
      <h2>Throttled Window Dimensions</h2>
      <p>Width: {width}px</p>
      <p>Height: {height}px</p>
      <p>Updates throttled to 100ms</p>
    </div>
  );
}

