import React, { useState, useEffect, useCallback } from 'react';

// Debounce utility function
const debounce = (func, delay) => {
  let timeoutId;
  return function(...args) {
    clearTimeout(timeoutId);
    timeoutId = setTimeout(() => func.apply(this, args), delay);
  };
};

// Custom hook to get debounced window dimensions
function useDebouncedWindowDimensions(delay = 200) {
  const [dimensions, setDimensions] = useState({
    width: typeof window !== 'undefined' ? window.innerWidth : 0,
    height: typeof window !== 'undefined' ? window.innerHeight : 0,
  });

  const updateDimensions = useCallback(
    debounce(() => {
      setDimensions({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    }, delay),
    [delay] // Recalculate debounced function if delay changes
  );

  useEffect(() => {
    if (typeof window === 'undefined') return;

    // Set initial dimensions immediately on mount
    updateDimensions(); 

    // Add event listener with debounced function
    window.addEventListener('resize', updateDimensions);

    // Cleanup: remove event listener on unmount
    return () => {
      window.removeEventListener('resize', updateDimensions);
    };
  }, [updateDimensions]); // Dependency on updateDimensions ensures effect re-runs if debounced function changes (e.g., if delay prop changes)

  return dimensions;
}

// Component to demonstrate the hook
function DebouncedWindowDisplay() {
  const { width, height } = useDebouncedWindowDimensions(500);

  return (
    <div style={{ padding: '20px', border: '1px solid #ddd', margin: '20px' }}>
      <h2>Debounced Window Dimensions</h2>
      <p>Width: {width}px</p>
      <p>Height: {height}px</p>
      <p>Updates only after 500ms of no resizing.</p>
    </div>
  );
}

