*/
.centered-container {
  width: 300px;
  height: 200px;
  border: 2px dashed #333;
  margin: 20px;
  font-family: sans-serif;
  display: block; /* Reset display for method-specific overrides */
}

/* Flexbox Method */
.centered-container--flexbox {
  display: flex;
  justify-content: center; /* Horizontal */
  align-items: center;    /* Vertical */
}

/* CSS Grid Method */
.centered-container--grid {
  display: grid;
  place-items: center; /* Horizontal and Vertical shorthand */
}

/* Absolute Positioning Method */
.centered-container--absolute {
  position: relative; /* Establish positioning context */
}

.centered-container--absolute > div {
  /* Assuming a single direct child for absolute centering */
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

