function analyzeVariables() {
  // 1. Number primitive (pass-by-value)
  let numA = 10;
  let numB = numA;
  console.log(`Initial: numA = ${numA}, numB = ${numB}`);
  numA = 20;
  // Expected: numB will remain 10 because numA holds a copy of the value; changing numA doesn't affect numB.
  console.log(`After numA = 20: numA = ${numA}, numB = ${numB}`); // Output: numA = 20, numB = 10

  // 2. Object literal (pass-by-reference - modifying content)
  let objX = { value: 100 };
  let objY = objX;
  console.log(`Initial: objX.value = ${objX.value}, objY.value = ${objY.value}`);
  objX.value = 200;
  // Expected: objY.value will also be 200 because objX and objY both point to the SAME object in memory.
  console.log(`After objX.value = 200: objX.value = ${objX.value}, objY.value = ${objY.value}`); // Output: objX.value = 200, objY.value = 200

  // 3. Object literal (pass-by-reference - reassigning reference)
  let objP = { data: 'Alpha' };
  let objQ = objP;
  console.log(`Initial: objP.data = ${objP.data}, objQ.data = ${objQ.data}`);
  objP = { data: 'Beta' }; // Reassign objP to a NEW object
  // Expected: objQ.data will remain 'Alpha' because objP was reassigned to a new object, but objQ still points to the ORIGINAL object.
  console.log(`After objP = { data: 'Beta' }: objP.data = ${objP.data}, objQ.data = ${objQ.data}`); // Output: objP.data = Beta, objQ.data = Alpha

  // Additional check with an array
  let arr1 = [1, 2];
  let arr2 = arr1;
  console.log(`Initial: arr1 = ${arr1}, arr2 = ${arr2}`);
  arr1.push(3);
  console.log(`After arr1.push(3): arr1 = ${arr1}, arr2 = ${arr2}`); // Expected: arr2 also has 3

  arr1 = [4, 5]; // Reassign arr1 to a new array
  console.log(`After arr1 = [4, 5]: arr1 = ${arr1}, arr2 = ${arr2}`); // Expected: arr2 remains [1,2,3]
}

analyzeVariables();

