    const b = 20;
    console.log(b); // Output: 20 (outside TDZ)
  }

  // Snippet 3
  console.log(c); // Output: undefined. 'var c' is hoisted and initialized to undefined. No TDZ.
  var c = 30;
  console.log(c); // Output: 30

  // Snippet 4: Function expression with TDZ
  // sayHello(); // Line 3: ReferenceError - 'sayHello' is in TDZ because it's a const-declared function expression.
  const sayHello = () => {
    console.log('Hello!');
  };
  sayHello(); // Output: 'Hello!' (outside TDZ)

  // Snippet 5: Nested scope with TDZ
  let outerVar = 'I am outer';
  if (true) {
