function flattenIterative(arr) {
  const stack = [...arr]; // Use a stack, initialize with array elements
  const result = [];

  while (stack.length > 0) {
    const element = stack.shift(); // Take elements from the front of the stack (mimicking queue for order)

    if (Array.isArray(element)) {
      // If it's an array, unshift its elements back to the stack (at the beginning to maintain order)
      // Using unshift + reverse or push + reverse at the end for stack behavior
      // For maintaining original order like recursion, we process from right to left if pushing to end
      // Or process from left to right if pushing to beginning of stack (unshift)
      // Let's use unshift to simulate recursive call order for depth-first traversal with a queue-like processing:
      // stack = [..., element_n-1, element_n, ...] -> process element_n, if array, unshift its children in reverse
      // to get correct order when shifted.
      // A simpler approach for iterative flatten to maintain order is to use a while loop with splice/concat for results
      // or use a stack with push/pop and reverse when pushing children.

      // Correct iterative flatten using a stack for DFS, maintaining order:
      // stack operations: push/pop. Push children in reverse order to process them correctly.
      const innerElements = element.reverse(); // Reverse so that when popped, they are in original order
      for (let i = 0; i < innerElements.length; i++) {
        stack.unshift(innerElements[i]); // Add to the beginning to process next
      }
    } else {
      result.push(element);
    }
  }

  return result;
}

// A more common and often simpler iterative flatten that maintains order:
function flattenIterativeQueue(arr) {
    const result = [];
    const queue = [...arr]; // Initialize a queue with the array elements

    while (queue.length > 0) {
        const current = queue.shift(); // Get the first element

        if (Array.isArray(current)) {
            // If it's an array, add its elements to the end of the queue
            // This correctly maintains order from left to right
            queue.push(...current);
        } else {
            result.push(current);
        }
    }
    return result;
}

