/**
 * Creates a higher-order filter function based on specified criteria.
 * @param {Object} criteria An object with key-value pairs to match.
 * @returns {function(Object): boolean} A predicate function for Array.prototype.filter.
 */
function createFilter(criteria) {
  const criteriaKeys = Object.keys(criteria);

  return function(item) {
    // Handle null/undefined items gracefully
    if (item === null || typeof item !== 'object') {
      return false;
    }

    // If no criteria are provided, all items match
    if (criteriaKeys.length === 0) {
      return true;
    }

    // Check if the item matches all criteria
    for (const key of criteriaKeys) {
      // If item doesn't have the property, or its value doesn't match, return false
      if (!item.hasOwnProperty(key) || item[key] !== criteria[key]) {
        return false;
      }
    }
    // If all criteria are matched, return true
    return true;
  };
}

