class CustomPromise {
  constructor(action) {
    this.state = "PENDING";
    this.value = undefined;
    this.thenCallbacks = [];
    this.errorCallbacks = [];

    const resolve = (value) => {
      setTimeout(() => this._resolve(value), 0);
    };
    const reject = (value) => {
      setTimeout(() => this._reject(value), 0);
    };

    try {
      action(resolve, reject);
    } catch (e) {
      reject(e);
    }
  }

  _resolve(value) {
    if (this.state === "PENDING") {
      this.state = "RESOLVED";
      this.value = value;
      // Execute all registered thenCallbacks asynchronously
      this.thenCallbacks.forEach(callback => callback(this.value));
      this.thenCallbacks = [];
      this.errorCallbacks = [];
    }
  }

  _reject(value) {
    if (this.state === "PENDING") {
      this.state = "REJECTED";
      this.value = value;
      // Execute all registered errorCallbacks asynchronously
      this.errorCallbacks.forEach(callback => callback(this.value));
      this.thenCallbacks = [];
      this.errorCallbacks = [];
    }
  }

  then(onFulfilled, onRejected) {
    // Return a new promise for chaining
    return new CustomPromise((newResolve, newReject) => {
      const handleFulfilled = (value) => {
        try {
          const result = onFulfilled ? onFulfilled(value) : value;
          newResolve(result);
        } catch (error) {
          newReject(error);
        }
      };

      const handleRejected = (reason) => {
        try {
          if (onRejected) {
            const result = onRejected(reason);
            newResolve(result); // If onRejected handles error, new promise resolves
          } else {
            newReject(reason); // Error propagation
          }
        } catch (error) {
          newReject(error);
        }
      };

      if (this.state === "RESOLVED") {
        setTimeout(() => handleFulfilled(this.value), 0);
      } else if (this.state === "REJECTED") {
        setTimeout(() => handleRejected(this.value), 0);
      } else { // PENDING
        this.thenCallbacks.push(handleFulfilled);
        this.errorCallbacks.push(handleRejected);
      }
    });
  }

  catch(onRejected) {
    return this.then(null, onRejected);
  }
}

// Usage for testing chaining:
console.log("Start of script");

let promise1 = new CustomPromise(resolve => {
  setTimeout(() => resolve(10), 100);
});

promise1
  .then(value => {
    console.log(`First then: ${value}`); // 10
    return value * 2;
  })
  .then(newValue => {
    console.log(`Second then: ${newValue}`); // 20
    return new CustomPromise(resolve => setTimeout(() => resolve(newValue + 5), 50)); // Simplified: doesn't truly wait like native
  })
  .then(finalValue => {
    console.log(`Third then: ${finalValue}`); // 25
    if (finalValue > 20) throw new Error("Value too high!");
    return finalValue;
  })
  .catch(error => {
    console.error("Caught error in chain:", error.message);
  })
  .then(() => console.log("This runs after catch or successful chain"));

let errorPromise = new CustomPromise((_, reject) => {
  setTimeout(() => reject("Error occurred!"), 200);
});

errorPromise
  .then(val => console.log("Should not run:", val))
  .catch(err => {
    console.error("Caught error in error chain:", err);
    return "Error handled"; // Returning a value from catch resolves next then
  })
  .then(msg => console.log("Next after handling error:", msg)); // "Error handled"

console.log("End of script");

