const moviesData = [
  { title: 'The Great Adventure', genre: 'Action', averageRating: 4.5 },
  { title: 'Epic Drama', genre: 'Drama', averageRating: 3.8 },
  { title: 'Comedy Night', genre: 'Comedy', averageRating: 4.1 },
  { title: 'Sci-Fi Odyssey', genre: 'Sci-Fi', averageRating: 4.7 },
  { title: 'Romantic Getaway', genre: 'Romance', averageRating: 3.5 },
  { title: 'Action Thriller', genre: 'Action', averageRating: 4.5 },
  { title: 'Another Drama', genre: 'Drama', averageRating: 3.9 }
];

/**
 * Processes a list of movie objects based on filtering, sorting, and mapping options.
 * @param {Array<Object>} movieList The list of movie objects.
 * @param {Object} options The processing options (minRating, genre, sortBy, outputFormat).
 * @returns {Array<Object>|Array<string>} The processed list of movies or movie titles.
 */
function processMovies(movieList, options = {}) {
  if (!Array.isArray(movieList)) {
    return [];
  }

  let result = [...movieList]; // Start with a shallow copy

  const { minRating = 0, genre = null, sortBy = null, outputFormat = null } = options;

  // 1. Filtering
  result = result.filter(movie => {
    let passesFilter = true;

    if (movie.averageRating < minRating) {
      passesFilter = false;
    }

    if (genre !== null && movie.genre !== genre) {
      passesFilter = false;
    }

    // Handle potentially malformed movie objects
    if (typeof movie.averageRating !== 'number' || typeof movie.title !== 'string' || typeof movie.genre !== 'string') {
        passesFilter = false;
    }

    return passesFilter;
  });

  // 2. Sorting
  if (sortBy) {
    result.sort((a, b) => {
      switch (sortBy) {
        case 'titleAsc':
          return a.title.localeCompare(b.title);
        case 'titleDesc':
          return b.title.localeCompare(a.title);
        case 'ratingAsc':
          return a.averageRating - b.averageRating;
        case 'ratingDesc':
          return b.averageRating - a.averageRating;
        default:
          return 0; // No specific sort
      }
    });
  }

  // 3. Mapping
  if (outputFormat === 'titlesOnly') {
    result = result.map(movie => movie.title);
  }

  return result;
}

