function getProducts() {
  return [
    { id: 1, name: 'Laptop Pro 15', price: 1200, category: 'Electronics' },
    { id: 2, name: 'Mechanical Keyboard', price: 150, category: 'Accessories' },
    { id: 3, name: 'Wireless Mouse', price: 75, category: 'Accessories' },
    { id: 4, name: 'External SSD 1TB', price: 180, category: 'Storage' },
    { id: 5, name: 'Gaming Monitor 27', price: 450, category: 'Electronics' },
    { id: 6, name: 'Webcam HD', price: 50, category: 'Peripherals' },
    { id: 7, name: 'USB-C Hub', price: 40, category: 'Accessories' },
    { id: 8, name: 'Laptop Stand', price: 60, category: 'Accessories' },
  ];
}

// Helper for normalizing strings for search
const normalizeString = str => str.toLowerCase().replace(/\s/g, '');

// Helper for checking if one string contains another (case & space insensitive)
const containsNormalized = (source, query) => {
  const normalizedSource = normalizeString(source);
  const normalizedQuery = normalizeString(query);
  return normalizedSource.includes(normalizedQuery);
};

function queryProducts({ search, sort, minPrice, maxPrice }) {
  let products = getProducts();

  // 1. Filter by price range
  products = products.filter(product => {
    const passesMin = minPrice === undefined || product.price >= minPrice;
    const passesMax = maxPrice === undefined || product.price <= maxPrice;
    return passesMin && passesMax;
  });

  // 2. Filter by search term
  if (search) {
    products = products.filter(product => 
      containsNormalized(product.name, search)
    );
  }

  // 3. Sort products
  if (sort) {
    const isDescending = sort[0] === '-';
    const sortField = isDescending ? sort.slice(1) : sort;

    products = [...products].sort((a, b) => {
      const valueA = a[sortField];
      const valueB = b[sortField];

      if (typeof valueA === 'string' && typeof valueB === 'string') {
        const comparison = valueA.localeCompare(valueB);
        return isDescending ? -comparison : comparison;
      } else {
        // Assume numeric or comparable non-string values
        return isDescending ? (valueB - valueA) : (valueA - valueB);
      }
    });
  }

  return products;
}

