const fetchAndMapData = async () => {
  try {
    // Fetch both posts and comments in parallel using Promise.all
    const [posts, comments] = await Promise.all([
      fetchAllPosts(),
      fetchAllComments()
    ]);

    // Function to get all comments for a specific post using filter
    const getCommentsForPost = postId =>
      comments.filter(comment => comment.postId === postId);

    // Map posts to their comments using reduce
    const postsWithComments = posts.reduce((result, post) => {
      result[post.id] = {
        ...post,
        comments: getCommentsForPost(post.id)
      };
      return result;
    }, {}); // Initialize accumulator as an empty object

    return postsWithComments;
  } catch (error) {
    console.error('Error fetching data:', error);
