const fetchAndMapDataRefactored = async () => {
  try {
    const posts = await fetchAllPosts();

    // Create an array of promises for fetching comments for each post
    // Use .then/.catch for each individual promise to handle errors gracefully
    const postsWithCommentPromises = posts.map(async post => {
      try {
        const comments = await fetchCommentsForPost(post.id);
        return { ...post, comments };
      } catch (error) {
