    const postsWithComments = await Promise.all(postsWithCommentPromises);

    // Transform the array into an object keyed by post ID (optional, if original structure desired)
    return postsWithComments.reduce((acc, post) => {
      acc[post.id] = post;
      return acc;
    }, {});

  } catch (error) {
    console.error('Error fetching data:', error);
    return {};
  }
};

