/**
 * Mock function to simulate fetching users from an API.
 * @returns {Promise<Array<{id: number, name: string}>>}
 */
const fetchUsers = () => {
  return new Promise(resolve => {
    setTimeout(() => {
      resolve([
        { id: 1, name: 'Alice' },
        { id: 2, name: 'Bob' }
      ]);
    }, 200);
  });
};

/**
 * Mock function to simulate fetching products from an API.
 * Products have a category and sales count.
 * @returns {Promise<Array<{id: number, name: string, category: string, salesCount: number}>>}
 */
const fetchProducts = () => {
  return new Promise(resolve => {
    setTimeout(() => {
      resolve([
        { id: 101, name: 'Laptop', category: 'Electronics', salesCount: 150 },
        { id: 102, name: 'Mouse', category: 'Electronics', salesCount: 200 },
        { id: 103, name: 'Keyboard', category: 'Electronics', salesCount: 100 },
        { id: 104, name: 'T-Shirt', category: 'Apparel', salesCount: 300 },
        { id: 105, name: 'Jeans', category: 'Apparel', salesCount: 250 }
      ]);
    }, 300);
  });
};

async function initializeDashboardData() {
  try {
    // Fetch both users and products concurrently
    const [users, products] = await Promise.all([
      fetchUsers(),
      fetchProducts()
    ]);

    // Calculate the most popular product category
    const categorySales = products.reduce((acc, product) => {
      acc[product.category] = (acc[product.category] || 0) + product.salesCount;
      return acc;
    }, {});

    let mostPopularCategory = null;
    let maxSales = 0;

    for (const category in categorySales) {
      if (categorySales[category] > maxSales) {
        maxSales = categorySales[category];
        mostPopularCategory = category;
      }
    }

    return {
      users,
      products,
      mostPopularCategory
    };
  } catch (error) {
    console.error('Failed to initialize dashboard data:', error);
    // Return default empty state on error
    return {
      users: [],
      products: [],
      mostPopularCategory: null
    };
  }
}

