if (!Array.prototype.deepFlatten) {
  Array.prototype.deepFlatten = function() {
    // Use reduce to iterate over the array elements
    return this.reduce((acc, item) => {
      // If the item is an array, recursively call deepFlatten on it
      // and concatenate the result to the accumulator
      if (Array.isArray(item)) {
        return acc.concat(item.deepFlatten());
      } else {
        // If the item is not an array, just add it to the accumulator
        return acc.concat(item);
      }
    }, []); // Initialize the accumulator as an empty array
  };
}

