tasks.solutionCode:
function parseConfigValue(value) {
  // Handle null or undefined first, as typeof null is 'object'
  if (value === null || typeof value === 'undefined') {
    return value;
  }

  // Process string values
  if (typeof value === 'string') {
    // Empty string to null
    if (value === '') {
      return null;
    }

    // Try to convert to number
    const numValue = Number(value);
    if (!isNaN(numValue) && String(numValue) === value) { // Check if it's a valid number string like '123' or '12.5' but not '123a'
        return numValue;
    }

    // Try to convert to boolean (case-insensitive)
    const lowerCaseValue = value.toLowerCase();
    if (lowerCaseValue === 'true') {
      return true;
    }
    if (lowerCaseValue === 'false') {
      return false;
    }

    // If none of the above, keep as original string
    return value;
  }

  // For non-string primitives (numbers, booleans), return as is
  return value;
}

