/**
 * Represents an item in the shopping cart.
 * @param {number} id - Unique ID of the item.
 * @param {string} name - Name of the item.
 * @param {number} price - Price of the item.
 */
class CartItem {
  constructor(id, name, price) {
    this.id = id;
    this.name = name;
    this.price = price;
  }
}

class ShoppingCart {
  constructor() {
    this._items = []; // Internal array to store cart items
  }

  /**
   * Adds an item to the cart. 
   * For simplicity, directly stores the item reference. For deep copy,
   * one would use `JSON.parse(JSON.stringify(item))` or a dedicated cloning utility.
   * @param {CartItem} item - The item to add.
   */
  addItem(item) {
    // 'this' refers to the ShoppingCart instance
    if (item && item.id && item.name && typeof item.price === 'number') {
      this._items.push(item);
    } else {
      console.warn('Invalid item provided to addItem:', item);
    }
  }

  /**
   * Removes an item from the cart by its ID.
   * @param {number} itemId - The ID of the item to remove.
   */
  removeItem(itemId) {
    // 'this' refers to the ShoppingCart instance
    this._items = this._items.filter(item => item.id !== itemId);
  }

  /**
   * Calculates the total price of all items in the cart.
   * @returns {number} The total price.
   */
  getTotal() {
    // 'this' refers to the ShoppingCart instance
    return this._items.reduce((total, item) => total + item.price, 0);
  }

  /**
   * Returns a copy of the cart contents to prevent external modification.
   * @returns {Array<CartItem>} A shallow copy of the items array.
   */
  getCartContents() {
    // 'this' refers to the ShoppingCart instance
    return [...this._items]; // Returns a shallow copy
  }
}

