function debounceLeading(func, delay, immediate = false) {
  let timerId;
  let lastArgs;
  let lastThis;
  let result;

  return function(...args) {
    lastArgs = args;
    lastThis = this;

    const isInvoked = timerId != null;

    if (!isInvoked && immediate) {
      result = func.apply(lastThis, lastArgs);
    }

    clearTimeout(timerId);
    timerId = setTimeout(() => {
      if (!immediate) {
        result = func.apply(lastThis, lastArgs);
      }
      timerId = null; // Clear the timer ID after execution
    }, delay);

    return result; // Return the result of the *last* invocation
  };
}

