function throttleLeadingAndTrailing(func, interval) {
  let timeoutId = null;
  let lastArgs;
  let lastThis;
  let lastResult;
  let lastRan = 0;

  const throttled = function(...args) {
    lastArgs = args;
    lastThis = this;

    const now = Date.now();
    const remaining = interval - (now - lastRan);

    if (remaining <= 0 || remaining > interval) {
      if (timeoutId) {
        clearTimeout(timeoutId);
        timeoutId = null;
      }
      lastRan = now;
      lastResult = func.apply(lastThis, lastArgs);
    } else if (!timeoutId) {
      timeoutId = setTimeout(() => {
        lastRan = Date.now();
        timeoutId = null;
        lastResult = func.apply(lastThis, lastArgs);
      }, remaining);
    }
    return lastResult;
  };

  return throttled;
}

// Usage Example:
const logActivity = throttleLeadingAndTrailing((message) => {
  console.log(`Activity logged: ${message} at ${new Date().toLocaleTimeString()}`);
