/**
 * Finds pairs of numbers in an array that sum to a specific target.
 * @param {number[]} nums The input array of numbers.
 * @param {number} target The target sum.
 * @returns {Array<[number, number]>} An array of pairs that sum to the target.
 */
function findPairsWithSum(nums, target) {
  const pairs = [];
  // Use a Map to store numbers encountered and their indices (or just their presence).
  // For this problem, we just need presence, so a Set or Map with boolean value works.
  const numMap = new Map(); 

  for (const num of nums) {
    const complement = target - num;

    // Check if the complement has been seen before
    if (numMap.has(complement)) {
      pairs.push([complement, num]); // Store the pair
    }

    // Add the current number to the map
    // We set a value (e.g., true) just to indicate presence.
    // If we needed indices, we'd store numMap.set(num, index);
    numMap.set(num, true);
  }

  return pairs;
}

// Example 1: Basic usage
