console.log(`Pairs for target ${targetSum3}:`, result3); // Expected: [[3,3], [2,4]] (order might vary)

/**
 * Finds a local maximum in an array.
 * A local maximum is an element that is greater than or equal to its neighbors.
 * @param {number[]} nums The input array of numbers.
 * @returns {number | null} The value of a local maximum, or null if the array is empty.
 */
function findLocalMaximum(nums) {
  if (!nums || nums.length === 0) {
    return null; // Handle empty array
  }
  if (nums.length === 1) {
    return nums[0]; // Single element is always a local maximum
  }

  // Check first element
  if (nums[0] >= nums[1]) {
    return nums[0];
  }

  // Check elements from index 1 to second-to-last
  for (let i = 1; i < nums.length - 1; i++) {
    if (nums[i] >= nums[i - 1] && nums[i] >= nums[i + 1]) {
      return nums[i]; // Found a local maximum
    }
  }

  // Check last element
  if (nums[nums.length - 1] >= nums[nums.length - 2]) {
    return nums[nums.length - 1];
  }

  return null; // Should ideally not be reached if a local maximum always exists (e.g., in a non-empty array)
}

// Example Usage:
console.log('Local max in [1, 2, 3, 4, 5]:', findLocalMaximum([1, 2, 3, 4, 5])); // Expected: 5
