```javascript
function findUniquePairsWithSum(nums, target) {
  const uniquePairs = [];
  const seenNums = new Map(); // Stores num -> count of occurrences
  const addedPairs = new Set(); // Stores stringified pairs to track uniqueness

  // First, count occurrences of each number
  for (const num of nums) {
    seenNums.set(num, (seenNums.get(num) || 0) + 1);
  }

  // Then iterate through the unique numbers in the map
  for (const [num, count] of seenNums.entries()) {
    const complement = target - num;

    // Case 1: num is the complement of itself (e.g., target = 6, num = 3)
    if (num === complement) {
      // Need at least two occurrences of this number
      if (count >= 2) {
        const pair = [num, num].sort((a, b) => a - b).join(',');
        if (!addedPairs.has(pair)) {
          uniquePairs.push([num, num]);
          addedPairs.add(pair);
        }
      }
    } 
    // Case 2: num and complement are different numbers
    else if (seenNums.has(complement)) {
      const pair = [num, complement].sort((a, b) => a - b).join(',');
      if (!addedPairs.has(pair)) {
        uniquePairs.push([num, complement].sort((a, b) => a - b));
        addedPairs.add(pair);
      }
    }
  }

  return uniquePairs;
}

// Test Cases
console.log(JSON.stringify(findUniquePairsWithSum([1, 1, 2, 3], 4))); // Expected: [[1,3],[2,2]] (order may vary)
console.log(JSON.stringify(findUniquePairsWithSum([2, 2, 2, 2], 4))); // Expected: [[2,2]]
console.log(JSON.stringify(findUniquePairsWithSum([1, 2, 3, 4, 5], 6))); // Expected: [[1,5],[2,4]]
console.log(JSON.stringify(findUniquePairsWithSum([0, 0, 0, 0], 0))); // Expected: [[0,0]]
console.log(JSON.stringify(findUniquePairsWithSum([1, 2, 3, 4, 5, 6], 7))); // Expected: [[1,6],[2,5],[3,4]]
console.log(JSON.stringify(findUniquePairsWithSum([7, 2, 4, 8, 3, 1, 10], 11))); // Expected: [[1,10],[2,9],[3,8],[4,7]] (9 is missing from input)

```

