/**
 * Finds a local maximum in an array.
 * @param {number[]} nums The input array of numbers.
 * @returns {number | null} The value of a local maximum, or null if the array is empty.
 */
function findAnyLocalMaximum(nums) {
  if (!nums || nums.length === 0) {
    return null; // Handle empty array
  }
  if (nums.length === 1) {
    return nums[0]; // Single element is always a local maximum
  }

  // Check first element
  // It's a local max if it's greater than or equal to its only neighbor (nums[1])
  if (nums[0] >= nums[1]) {
    return nums[0];
  }

  // Check elements from index 1 to second-to-last
  for (let i = 1; i < nums.length - 1; i++) {
    // An element is a local max if it's >= both its left and right neighbors
    if (nums[i] >= nums[i - 1] && nums[i] >= nums[i + 1]) {
      return nums[i]; // Found a local maximum, return it
    }
  }

  // Check last element
  // It's a local max if it's greater than or equal to its only neighbor (nums[length - 2])
  if (nums[nums.length - 1] >= nums[nums.length - 2]) {
    return nums[nums.length - 1];
  }

  // This return statement should theoretically not be reached if the array is non-empty
  // as a local maximum is guaranteed to exist in any non-empty array.
  return null; 
}

