/**
 * Moves all occurrences of a specific target number to the end of an array in-place.
 * Uses a two-pointer approach.
 * @param {Array<number>} array The array to modify.
 * @param {number} target The number to move to the end.
 * @returns {Array<number>} The modified array with target elements at the end.
 */
function moveElementToEnd(array: number[], target: number): number[] {
  if (!array || array.length === 0) {
    return [];
  }

  let i = 0; // Slow pointer, points to where the next non-target element should go
  let j = 0; // Fast pointer, iterates through the array

  // Move all non-target elements to the front using the fast pointer (j)
  // and placing them at the slow pointer's position (i).
  while (j < array.length) {
    if (array[j] !== target) {
      // If the element at j is not the target, swap it with the element at i
      // and increment i to point to the next available slot for a non-target element.
      [array[i], array[j]] = [array[j], array[i]];
      i++;
    }
    j++; // Always increment j to continue scanning the array
  }

  return array;
}

// Example Usage:
const inputArray1 = [2, 9, 1, 5, 2, 3, 1, 2, 7, 4, 3, 8, 29, 2, 4, 6, 54, 32, 2, 100];
