/**
 * Rotates a square matrix 90 degrees clockwise in-place.
 * @param {number[][]} matrix The square matrix to rotate.
 * @returns {number[][]} The rotated matrix (same reference as input).
 */
function rotateMatrix(matrix: number[][]): number[][] {
  if (!matrix || matrix.length === 0 || matrix[0].length === 0) {
    return matrix; // Handle empty or invalid matrix
  }
  const n = matrix.length;

  // Check if it's a square matrix (optional, but common for this problem type)
  if (matrix.some(row => row.length !== n)) {
    console.warn("Warning: Matrix is not square. Rotation might not be as expected.");
    // Depending on requirements, might throw an error or handle non-square matrices differently
  }

  // 1. Transpose the matrix (swap rows with columns)
  // Iterate only the upper triangle to avoid double swapping
  for (let i = 0; i < n; i++) {
    for (let j = i; j < n; j++) {
      // Swap elements at positions [i,j] and [j,i]
      [matrix[i][j], matrix[j][i]] = [matrix[j][i], matrix[i][j]];
    }
  }

  // 2. Reverse each row
  for (let i = 0; i < n; i++) {
    matrix[i].reverse();
  }

  return matrix;
}

// Example Usage:
const matrix1 = [
