// Iterative approach using a stack for DFS traversal
function sumNestedArrayIterative(arr: (number | any[])[]): number {
  let sum = 0;
  const stack: (number | any[])[] = [...arr]; // Initialize stack with top-level elements

  while (stack.length > 0) {
    const item = stack.pop(); // Pop from end for DFS (or shift from beginning for BFS if using queue)

    if (Array.isArray(item)) {
      // Push elements in reverse order to maintain correct order when popping
      // For example, if item is [a, b, c], push c, then b, then a so a is processed first.
      for (let i = item.length - 1; i >= 0; i--) {
        stack.push(item[i]);
      }
    } else if (typeof item === 'number') {
      sum += item;
    }
  }
  return sum;
}

const nestedArray = [4, 5, 7, 8, [5, 7, 9, [3, 5, 7]]];
console.log('Iterative sum:', sumNestedArrayIterative(nestedArray)); // 60
```

