  // This would typically involve iterating and shifting elements, or using an auxiliary array.

  // Solution using auxiliary array to preserve relative order (simpler to implement):
  const result: number[] = [];
  const evens: number[] = [];
  for (const num of array) {
    if (num % 2 === 0) {
      evens.push(num);
    } else {
      result.push(num);
    }
  }
  return result.concat(evens);
}

