type Question = {
  id: string;
  text: string;
  options?: string[];
  answer?: string;
};

function getShuffledQuestions<T>(questions: T[]): T[] {
  const shuffledArray = [...questions]; // Create a shallow copy to avoid mutating original
  let currentIndex = shuffledArray.length;
  let randomIndex: number;

  // While there remain elements to shuffle.
  while (currentIndex !== 0) {
    // Pick a remaining element.
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;

    // And swap it with the current element.
    [shuffledArray[currentIndex], shuffledArray[randomIndex]] = [
      shuffledArray[randomIndex],
      shuffledArray[currentIndex],
    ];
  }

  return shuffledArray;
}

const quizManager = (() => {
  let currentShuffledQuestions: Question[] = [];
  let currentIndex: number = 0;
  let originalQuestions: Question[] = []; // Store original for reset

  function _shuffle<T>(array: T[]): T[] {
    let m = array.length, t, i;
    while (m) {
      i = Math.floor(Math.random() * m--);
      t = array[m];
      array[m] = array[i];
      array[i] = t;
    }
    return array;
  }

  return {
    initialize: (questions: Question[]) => {
      originalQuestions = questions; // Store reference to original
      currentShuffledQuestions = _shuffle([...questions]); // Shuffle a copy
      currentIndex = 0;
    },
    getNextQuestion: (): Question | null => {
      if (currentIndex < currentShuffledQuestions.length) {
        return currentShuffledQuestions[currentIndex++];
      }
      return null; // All questions exhausted
    },
    resetQuiz: () => {
      // Re-initialize with a fresh shuffle of the original questions
      quizManager.initialize(originalQuestions);
    }
  };
})();

// Example Usage:
const quizQuestions: Question[] = [
  { id: 'q1', text: 'What is 2+2?' },
  { id: 'q2', text: 'Capital of France?' },
  { id: 'q3', text: 'First programming language?' }
];

console.log('\n--- getShuffledQuestions ---');
const shuffled1 = getShuffledQuestions(quizQuestions);
console.log('Shuffled questions (new array):', shuffled1);
console.log('Original questions (unmutated):', quizQuestions);

console.log('\n--- Quiz Manager ---');
quizManager.initialize(quizQuestions);
console.log('Next 1:', quizManager.getNextQuestion());
console.log('Next 2:', quizManager.getNextQuestion());
console.log('Next 3:', quizManager.getNextQuestion());
console.log('Next 4 (exhausted):', quizManager.getNextQuestion());

console.log('\n--- Reset Quiz ---');
quizManager.resetQuiz();
console.log('Next 1 after reset:', quizManager.getNextQuestion());
console.log('Next 2 after reset:', quizManager.getNextQuestion());
console.log('Next 3 after reset:', quizManager.getNextQuestion());

