function sumNumbersInNestedArray(arr: any[]): number {
  let totalSum = 0;

  function processArray(array: any[]) {
    for (const item of array) {
      if (Array.isArray(item)) {
        processArray(item); // Recursively process nested arrays
      } else if (typeof item === 'number') {
        totalSum += item;
      }
      // Else, ignore other types
    }
  }

  processArray(arr);
  return totalSum;
}

// Alternative using Array.prototype.flat() (if allowed/preferred):
function sumNumbersInNestedArrayFlat(arr: any[]): number {
  return arr.flat(Infinity) // Flatten array completely
            .filter(item => typeof item === 'number') // Keep only numbers
            .reduce((acc, curr) => acc + curr, 0); // Sum them up
}

// Example Usage:
const data1 = [1, [2, [3, 4]], 5, 'hello', [6, [7, null]], 8, true, {}];
console.log('Recursive Sum:', sumNumbersInNestedArray(data1)); // Expected: 36
console.log('Flat Method Sum:', sumNumbersInNestedArrayFlat(data1)); // Expected: 36

