function customFlat(arr: any[], depth: number = 1): any[] {
  const result: any[] = [];

  // Use a recursive helper function with an accumulator and current depth
  function flattenRecursive(currentArray: any[], currentDepth: number) {
    for (const item of currentArray) {
      // If item is an array AND we still have depth to flatten
      if (Array.isArray(item) && currentDepth > 0) {
        // Recurse with decremented depth
        flattenRecursive(item, currentDepth - 1);
      } else {
        // Otherwise, push the item (either not an array or no more depth to flatten)
        result.push(item);
      }
    }
  }

  flattenRecursive(arr, depth);
  return result;
}

// Example Usage:
const nestedArr = [1, [2, [3, 4]], 5, [6, [7, 8]]];

console.log('Original:', JSON.stringify(nestedArr));
console.log('Flat (depth 1):', JSON.stringify(customFlat(nestedArr))); // [1, 2, [3, 4], 5, 6, [7, 8]]
console.log('Flat (depth 2):', JSON.stringify(customFlat(nestedArr, 2))); // [1, 2, 3, 4, 5, 6, 7, 8]
console.log('Flat (depth Infinity):', JSON.stringify(customFlat(nestedArr, Infinity))); // [1, 2, 3, 4, 5, 6, 7, 8]
console.log('Flat (depth 0):', JSON.stringify(customFlat(nestedArr, 0))); // [1, [2, [3, 4]], 5, [6, [7, 8]]] (no flattening)
console.log('Flat (empty):', JSON.stringify(customFlat([]))); // []
