function moveZeros(nums: number[]): void {
  if (!nums || nums.length === 0) {
    return; // Handle empty or invalid array
  }

  let nonZeroPointer = 0; // Pointer for the next position to place a non-zero element

  // Iterate through the array with a readPointer
  for (let readPointer = 0; readPointer < nums.length; readPointer++) {
    // If the current element is non-zero, move it to the nonZeroPointer position
    if (nums[readPointer] !== 0) {
      nums[nonZeroPointer] = nums[readPointer];
      nonZeroPointer++;
    }
  }

  // Fill the remaining positions from nonZeroPointer to the end with zeros
  for (let i = nonZeroPointer; i < nums.length; i++) {
    nums[i] = 0;
  }
}

// Alternative using swaps (slightly different variant of two-pointer)
// This one combines both passes into one, but might do unnecessary swaps.
function moveZerosWithSwaps(nums: number[]): void {
  if (!nums || nums.length === 0) return;

  let i = 0; // Pointer for the last non-zero position found so far
  for (let j = 0; j < nums.length; j++) {
    if (nums[j] !== 0) {
      // If the element at 'j' is non-zero and 'i' is not at the same position,
      // swap them. This places non-zeros at 'i' and implicitly moves zeros out.
      if (i !== j) {
        [nums[i], nums[j]] = [nums[j], nums[i]];
      }
      i++;
    }
  }
}

// Example Usage:
const arr1 = [0, 1, 0, 3, 12];
moveZeros(arr1);
console.log('moveZeros([0,1,0,3,12]):', arr1); // Expected: [1, 3, 12, 0, 0]

