console.log('moveZeros([1,2,3]):', arr3); // Expected: [1, 2, 3]

const arr4 = [0, 0, 0];
moveZeros(arr4);
console.log('moveZeros([0,0,0]):', arr4); // Expected: [0, 0, 0]

const arr5 = [];
moveZeros(arr5);
console.log('moveZeros([]):', arr5); // Expected: []

const arr6 = [1];
