function removeDuplicates(nums: number[]): number {
  if (!nums || nums.length === 0) {
    return 0;
  }

  let writePointer = 1; // Pointer for the next position where a unique element should be written

  // readPointer iterates through the array starting from the second element
  for (let readPointer = 1; readPointer < nums.length; readPointer++) {
    // If the current element (at readPointer) is different from the previous unique element (at writePointer - 1)
    if (nums[readPointer] !== nums[writePointer - 1]) {
      nums[writePointer] = nums[readPointer];
      writePointer++;
    }
  }

  // The writePointer now indicates the new length of the array with unique elements
  return writePointer;
}

// Example Usage:
