function findMostConsecutiveCharExtended(str) {
  if (str.length === 0) {
    return { char: null, count: 0 }; // Return null char and 0 count for empty string
  }

  let maxChar = str[0];
  let maxCount = 1;
  let currentChar = str[0];
  let currentCount = 1;

  for (let i = 1; i < str.length; i++) {
    if (str[i] === currentChar) {
      currentCount++;
    } else {
      if (currentCount > maxCount) {
        maxCount = currentCount;
        maxChar = currentChar;
      }
      currentChar = str[i];
      currentCount = 1;
    }
  }

  if (currentCount > maxCount) {
    maxCount = currentCount;
    maxChar = currentChar;
  }

  return { char: maxChar, count: maxCount }; // Return an object
}

