tasks.solutionCode:
function spiralOrder(matrix) {
  if (!matrix.length) return [];

  const result = [];
  let top = 0;
  let bottom = matrix.length - 1;
  let left = 0;
  let right = matrix[0].length - 1;
  let direction = 0; // 0: right, 1: down, 2: left, 3: up

  while (top <= bottom && left <= right) {
    if (direction === 0) {
      // Move right
      for (let i = left; i <= right; i++) {
        result.push(matrix[top][i]);
      }
      top++;
    } else if (direction === 1) {
      // Move down
      for (let i = top; i <= bottom; i++) {
        result.push(matrix[i][right]);
      }
      right--;
    } else if (direction === 2) {
      // Move left (only if top is still <= bottom, avoids double counting in single row/column cases)
      if (top <= bottom) { 
        for (let i = right; i >= left; i--) {
          result.push(matrix[bottom][i]);
        }
      }
      bottom--;
    } else if (direction === 3) {
      // Move up (only if left is still <= right, avoids double counting in single row/column cases)
      if (left <= right) {
        for (let i = bottom; i >= top; i--) {
          result.push(matrix[i][left]);
        }
      }
      left++;
    }

    direction = (direction + 1) % 4; // Cycle direction
  }

  return result;
}

