function reverseLinkedListRecursive(head) {
  // Base case: If head is null or only one node, it's already reversed
  if (head === null || head.next === null) {
    return head;
  }

  // Recursively reverse the rest of the list
  // newHead will be the last node of the original list (and new head)
  const newHead = reverseLinkedListRecursive(head.next);

  // Attach the current head to the end of the reversed sublist
  // head.next is the second node in the original list.
  // After recursive call, head.next.next points back to head.
  head.next.next = head;

  // Set current head's next to null to break the original forward link
  // This current head will become the new tail of its reversed sublist
  head.next = null;

  // Return the new head of the fully reversed list
  return newHead;
}

