function getStringConsecutiveAnalysis(str) {
  if (!str || str.length === 0) {
    return {
      mostConsecutiveChar: '',
      maxConsecutiveCount: 0,
      allMaxChars: [],
      firstCharSequence: { char: '', count: 0 },
      totalDistinctChars: 0
    };
  }

  let maxChar = str[0];
  let maxCount = 1;
  let allMaxChars = [str[0]];

  let currentChar = str[0];
  let currentCount = 1;

  let firstCharSequence = { char: str[0], count: 1 }; // First char always has at least 1 count
  let distinctChars = new Set();
  distinctChars.add(str[0]);

  for (let i = 1; i < str.length; i++) {
    distinctChars.add(str[i]);

    if (str[i] === currentChar) {
      currentCount++;
    } else {
      // Check and update for max consecutive
      if (currentCount > maxCount) {
        maxCount = currentCount;
        maxChars = [currentChar];
      } else if (currentCount === maxCount) {
        allMaxChars.push(currentChar);
      }

      // Reset for new sequence
      currentChar = str[i];
      currentCount = 1;
    }

    // Update firstCharSequence if it's still processing the very first sequence
    if (i < str.length && str[i] === firstCharSequence.char && firstCharSequence.count === i) {
        firstCharSequence.count++;
    } else if (i === 1 && str[i] !== firstCharSequence.char) {
        // If the second char is different, the first sequence is just 1
        // This handles cases like 'abc', where firstCharSequence.count would be wrongly 2 for 'a' if not handled.
        // No, this needs to be simpler: first sequence count is set once if the char changes. 
        // Or more simply: just check it after the loop if the first char was isolated.
    }
  }

  // Final check for the last character sequence
  if (currentCount > maxCount) {
    maxCount = currentCount;
    maxChars = [currentChar];
  } else if (currentCount === maxCount) {
    allMaxChars.push(currentChar);
  }

  // Re-calculate firstCharSequence more reliably
  let firstSeqChar = str[0];
  let firstSeqCount = 1;
  for(let i = 1; i < str.length; i++) {
      if (str[i] === firstSeqChar) {
          firstSeqCount++;
      } else {
          break; // First sequence ended
      }
  }
  firstCharSequence = { char: firstSeqChar, count: firstSeqCount };

  return {
    mostConsecutiveChar: maxChar,
    maxConsecutiveCount: maxCount,
    allMaxChars: Array.from(new Set(allMaxChars)), // Remove duplicates for allMaxChars
    firstCharSequence: firstCharSequence,
    totalDistinctChars: distinctChars.size
  };
}

