class TreeNode {
  constructor(value) {
    this.value = value;
    this.left = null;
    this.right = null;
  }
}

function preorderTraversalIterative(root) {
  if (!root) return [];
  const result = [];
  const stack = [root];
  while (stack.length > 0) {
    const node = stack.pop();
    result.push(node.value);
    if (node.right) {
      stack.push(node.right);
    }
    if (node.left) {
      stack.push(node.left);
    }
  }
  return result;
}

function inorderTraversalIterative(root) {
  const result = [];
  const stack = [];
  let current = root;
  while (current !== null || stack.length > 0) {
    while (current !== null) {
      stack.push(current);
      current = current.left;
    }
    current = stack.pop();
    result.push(current.value);
    current = current.right;
  }
  return result;
}

function postorderTraversalIterative(root) {
  if (!root) return [];
  const result = [];
  const stack1 = [root]; // Main stack
  const stack2 = [];    // Output stack (used for reversing order)

  while (stack1.length > 0) {
    const node = stack1.pop();
    stack2.push(node.value); // Push to output stack

    if (node.left) {
      stack1.push(node.left);
    }
    if (node.right) {
      stack1.push(node.right);
    }
  }
  // Pop from stack2 to get in correct Postorder (Left-Right-Root)
  while(stack2.length > 0) {
      result.push(stack2.pop());
  }
  return result;
}

