class TreeNode {
  constructor(value) {
    this.value = value;
    this.left = null;
    this.right = null;
  }
}

function preorderTraversal(root) {
  const result = [];
  const stack = [];

  if (!root) {
    return result;
  }

  stack.push(root);

  while (stack.length > 0) {
    // Pop the top node from the stack
    const current = stack.pop();

    // Process the current node (add to result)
    result.push(current.value);

    // Push right child first (to process left child first since it's a stack)
    if (current.right) {
      stack.push(current.right);
    }

    // Push left child
    if (current.left) {
      stack.push(current.left);
    }
  }

  return result;
}

