// Solution for inorderTraversal iteratively
class TreeNode {
  constructor(value) {
    this.value = value;
    this.left = null;
    this.right = null;
  }
}

function inorderTraversal(root) {
  const result = [];
  const stack = [];
  let current = root;

  while (current !== null || stack.length > 0) {
    // Traverse to the leftmost node, pushing nodes onto stack along the way
    while (current !== null) {
      stack.push(current);
      current = current.left;
    }

    // Pop a node from stack (this is the leftmost unvisited node)
    current = stack.pop();
    result.push(current.value);

    // Move to the right child to process its subtree
    current = current.right;
  }

  return result;
}

