function flattenObject(obj, prefix = '') {
  return Object.keys(obj).reduce((result, key) => {
    const currentVal = obj[key];
    const newKey = prefix ? `${prefix}.${key}` : key;

    if (typeof currentVal === 'object' && currentVal !== null) {
