        // Handle arrays by iterating over elements
        currentVal.forEach((item, index) => {
          // Recursively flatten array elements, using bracket notation for key
          const arrayElementKey = `${newKey}[${index}]`;
          if (typeof item === 'object' && item !== null) {
            Object.assign(result, flattenObject(item, arrayElementKey));
          } else {
            result[arrayElementKey] = item;
          }
        });
      } else {
        // Handle nested objects
        Object.assign(result, flattenObject(currentVal, newKey));
      }
    } else {
