function unflattenObject(flattenedObj) {
  const result = {};

  for (const key in flattenedObj) {
    const value = flattenedObj[key];
    const parts = key.split('.');
    let current = result;

    for (let i = 0; i < parts.length; i++) {
      let part = parts[i];

      // Check for array notation like 'key[index]'
      const arrayMatch = part.match(/(.*)\[(\d+)\]$/);
      if (arrayMatch) {
        const arrayKey = arrayMatch[1];
        const index = parseInt(arrayMatch[2], 10);

        if (!current[arrayKey]) {
          current[arrayKey] = [];
        }
        if (!Array.isArray(current[arrayKey])) {
          // Handle collision: if arrayKey already exists and is not an array
          console.warn(`Path collision: '${arrayKey}' is not an array but key '${key}' expects it to be.`);
          // Decide how to handle: overwrite, skip, or throw error
          current[arrayKey] = []; // Force to array
        }

        // If this is the last part, assign the value
        if (i === parts.length - 1) {
          current[arrayKey][index] = value;
        } else {
          // If not last part, ensure nested object/array exists
          if (!current[arrayKey][index] || typeof current[arrayKey][index] !== 'object' || Array.isArray(current[arrayKey][index])) {
            current[arrayKey][index] = {};
          }
          current = current[arrayKey][index];
